t3.Ar_Id = t1.Ar_id
) t6
LEFT JOIN
,
( SELECT t1.PROC_INST_ID_, t2.GROUP_ID_, t1.TASK_DEF_KEY_ FROM act_ru_task t1, act_ru_identitylink t2 WHERE t1.ID_ = t2.TASK_ID_
AND t2.GROUP_ID_ = #{usrTp,jdbcType=VARCHAR}
) t4
ON
where
t6.PROCESS_INST_ID = t4.PROC_INST_ID_
ORDER BY ${orderBy}
) t8 where 1=1

